import React, { useState } from "react";
import styled from "styled-components";
import pencil from "../assets/pencil.png";
import whitePencil from "../assets/whitepencil.png";
import x from "../assets/close.png";
import ModalTimer from "./ModalTimer";

const Content = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 31px;
  border-bottom: 1px solid var(--middle);
  box-sizing: border-box;
  height: 67px;
  font-size: 20px;

  .green {
    width: 180px;
    display: fixed;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }

  .name {
    width: 232px;
  }
  .time {
    font-weight: bold;
    display: flex;
    justify-content: center;
    color: var(--middle);
    width: 102px;
  }

  .edit {
    width: 19.1px;
    height: 19.1px;
    background: url(${pencil});
    background-size: 100%;
  }
  .delete {
    width: 19.1px;
    height: 19.1px;
    background: url(${x});
    background-size: 100%;
  }

  :hover {
    background-color: var(--middle);
    color: white;
    .name {
      color: white;
    }
    .time {
      color: white;
    }
    .edit {
      width: 19.1px;
      height: 19.1px;
      background: url(${whitePencil});
      background-size: 100%;
    }
    .delete {
      width: 19.1px;
      height: 19.1px;
      background: url(${x});
      background-size: 100%;
    }
  }
`;

const TimerListContent = ({ timer, onRemove /*, openModal */ }) => {
  const { id, name, duration } = timer;
  const [modalOpen, setModalOpen] = useState(false);
  const [newName, setNewName] = useState(name);

  const openModal = (id, name) => {
    setModalOpen(true);
  };
  const closeModal = () => {
    setModalOpen(false);
  };
  const onInputChange = (e) => {
    setNewName(e.target.value);
  };

  const onSubmit = () => {
    console.log({ timer_name: newName });
  };

  return (
    <>
      <ModalTimer
        open={modalOpen}
        close={closeModal}
        onInputChange={onInputChange}
        onSubmit={onSubmit}
      ></ModalTimer>
      <Content>
        <div class="name">{name}</div>
        <div class="green">
          <div class="time">{duration}</div>
          <div class="edit" onClick={openModal}></div>
          <div class="delete" onClick={() => onRemove(id)}></div>
        </div>
      </Content>
    </>
  );
};

export default TimerListContent;
